module.exports = function resolveCandidates(symbol, env, scopes) {
  const candidates = []
  for (const binding of env[symbol]) {
    const bindingScopes = binding.scopes || new Set()
    let found = true
    for (const scope of bindingScopes) {
      if (!scopes.has(scope)) {
        found = false
        break
      }
    }
    if (!found) {
      continue
    }
    candidates.push(binding)
  }
  return candidates
}
