const generate = require("@babel/generator").default

module.exports = function print(syntax) {
  if (Array.isArray(syntax)) {
    return `(${syntax.map((item) => print(item)).join(" ")})`
  }
  switch (typeof syntax) {
    case "object":
      switch (syntax.type) {
        case "ArrayExpression":
          return print(syntax.elements)
        case "SymbolLiteral":
          return Symbol.keyFor(syntax.name)
        case "StringLiteral":
        case "NumericLiteral":
          return generate(syntax).code
      }
  }
}
