const generate = require("@babel/generator").default

module.exports = function print(syntax) {
  switch (syntax.type) {
    case "CallExpression":
      return `(${print(syntax.callee)}${
        syntax.arguments.length > 0
          ? ` ${syntax.arguments.map(print).join(" ")}`
          : ""
      })`
  }
  return generate(syntax).code
}
