class InputStream {
  constructor(input) {
    this.pos = 0
    this.input = input
  }

  peekChar() {
    return this.input.charAt(this.pos)
  }

  readChar() {
    return this.input.charAt(this.pos++)
  }

  eof() {
    return this.pos === this.input.length
  }

  toString() {
    return `#InputStream { pos: ${this.pos}, input: "${this.input}"}`
  }
}

module.exports = InputStream
