import { identifier } from "@babel/types"

module.exprots = function rest(form) {
  switch (form.type) {
    case "CallExpression":
      return form.arguments
    case "BlockStatement":
      return form.body
    case "MemberExpression":
      return [form.object, form.property]
    case "VariableDeclaration":
      return [identifier(form.kind), ...form.declarations]
    case "VariableDeclarator":
      return form.init && [form.init]
  }
}
