function ReturnStatement(form, env) {
  return {
    type: "ReturnStatement",
    argument: form.arguments[0] && expand(form.arguments[0], env),
  }
}

ReturnStatement.type = "ReturnStatement"
ReturnStatement.helpers = {
  nth(form, n) {
    return n === 0 ? { type: "Identifier", name: "return" } : form.argument
  },
}

module.exports = {
  return: ReturnStatement,
}
