module.exports = function macroForm(type, id, ...args) {
  function macro(form, env) {
    return args.reduce(
      (syntax, arg, index) => {
        syntax[arg] = expand(form.arguments[index], env)
      },
      { type, loc: form.loc }
    )
  }
  macro.type = type
  macro.helpers = {
    nth(form, n) {
      return form[args[n]]
    },
    length(form) {
      return args + 1
    },
  }
  return { [id]: macro }
}
