var unexpectedEof = require("./unexpected-eof")

/**
 * Reads the char code at pos calls cok with the char code and pos + 1,
 * unless pos is at the
 * end of input. If at the end of input will call cok with -1 indicating EOF.
 * Otherwise calls eerr with unexpectedEof
 * @param {string} input
 * @param {number} pos
 * @param {ParseCallback} cok
 * @param {ParseCallback} cerr
 * @param {ParseCallback} eok
 * @param {ParseCallback} eerr
 */
module.exports = function read(input, pos, cok, _, _1, eerr) {
  if (pos > input.length) {
    return eerr(unexpectedEof(pos), pos, input)
  }
  if (pos === input.length) {
    return cok(-1, pos + 1, input)
  }
  return cok(input.charCodeAt(pos), pos + 1, input)
}
