/**
 * Creates an Unexpected error.
 * @param {number} pos - The position in the input stream that the error
 *   occurred at
 * @param {string} expected - The expected value
 * @param {any} actual - The actual value
 */
module.exports = function unexpected(pos, expected, actual) {
  return new Error(
    "Unexpected input at positon: " +
      pos +
      ': Expected "' +
      expected +
      '" found "' +
      actual +
      '"'
  )
}
